//importações de bibliotecas
import React, {useState} from "react";


//importações de compotentes
import { Container, Form } from "./styles";
import Input from "../../Components/input/Input";
import Botao from "../../Components/Botao/Botao";

//Validações
import { validarEmail, validarSenha } from "./Validadores";

/*------------------------------------------ARRUMAR ARQUIVO DE IMPORTS-------------------------------------------------------------- */

const Login = () => {

    const [loading, setLoading] = useState(false);
    const [Form, setForm] = useState([]);
    
    //Botão
    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            setLoading(true);
            alert('Login');
            setLoading(false);
        } catch (err) {
            alert("Deu ruim"  + err)
        }
    }
    
    //input
    const handleChange = (event) => {
        console.log("Digitando... ", event.target.name, event.target.value);
        setForm({...Form, [event.target.name]: event.target.value});
    }
    console.log("Form valido? ", validadorInput())

    const validadorInput = () => {
        return validarEmail(Form.email) && validarSenha(Form.senha);
        console.log('Form ' + Form)
    }



  return (
    <Container>
      <Form>
        <Input
          name="name"
          placeholder="Digite seu nome de usuário"
          onChange={handleChange}
          type="text"
        />
        <Input
          name="Email"
          placeholder="Digite seu Email"
          onChange={handleChange}
          type="email"
        />
        <Input
          name="senha"
          placeholder="Digite sua senha"
          onChange={handleChange}
          type="password"
        />
        <Botao 
            type = 'submit'
            text = 'Entrar'
            onClick = {handleSubmit}
            disabled
        />
        <h1>Faça o seu login</h1>
        <div>
          <p>Não possui conta</p>
          <p>Cadastrar</p>
        </div>
      </Form>
    </Container>
  );
};

export default Login; //tudo que não for export default se importa entre chaves{}
